package sdk

import (
	"fmt"
	"log"
	"testing"
)

func TestMasking(t *testing.T) {
	// This test shows that GitHub masking works, even for logs generated by Golang program.
	// More on the add-mask flag: https://docs.github.com/en/actions/writing-workflows/choosing-what-your-workflow-does/workflow-commands-for-github-actions#masking-a-value-in-a-log.
	fmt.Printf("::add-mask::%s\n", "ABC")

	// In CI, the below logs will be masked and the output will be: `aa-***-aa`.
	t.Logf("t.Log: aa-%s-aa", "ABC")
	fmt.Printf("fmt.Printf: aa-%s-aa\n", "ABC")
	log.Printf("log.Printf: aa-%s-aa", "ABC")
}
